INSERT INTO `lesson` VALUES (1,'Matematica Maya','Lección para que puedas aprender todo sobre la matemática maya '),(3,'Calendario Cholq\'ij','El Cholqi\'j es un calendario sagrado del pueblo maya, compuesto por 260 días dividos en 13 meses. También es conocido como calendario ritual o calendario sagrado o Tzolkin.'),(4,'Calendario Haab','El calendario solar maya, llamado Haab\', es una cuenta de 365 días y por lo tanto se aproxima al año solar es el calendario civil, agrícola Maya, es el calendario de los reyes Mayas.');
