INSERT INTO `question` VALUES (1,1,'Como es llamado el sistema de numeración Maya?',NULL,NULL,25),(2,1,'De que esta compuesto el sistema de numeración Maya',NULL,NULL,25),(3,1,'Que inventaron los Mayas',NULL,NULL,25),(4,1,'De que son los organizadores los Mayas',NULL,NULL,25),(5,2,'Como es identificado el cero en la numeración maya',NULL,NULL,25),(6,2,'Como se identifica el cinco en la numeración maya',NULL,NULL,25),(7,2,'Como se identifica la unidad en la numeración maya',NULL,NULL,25),(8,2,'Hasta que numero llegan las primeras unidades en el sistema de numeración maya',NULL,NULL,25),(9,3,'Al calendario cholq\'ij también se le conoce como:',NULL,NULL,5),(10,3,'¿Cuántos días tiene el calendario cholq\'ij?',NULL,NULL,5),(11,3,'¿Cuántos meses tiene el calendario cholq\'ij?',NULL,NULL,5),(12,4,'Se dice que el calendario cholq\'ij se relaciona con:',NULL,NULL,5),(13,4,'¿Cuántos días tiene el calendario cholq\'ij?',NULL,NULL,5),(14,4,'¿Con qué planeta relacionan al calendario cholq\'ij?',NULL,NULL,10),(15,4,'Son símbolos que representan y vinculan a cada persona con el ecosistema, creando así el equilibrio',NULL,NULL,5),(16,4,'El calendario cholq\'ij utiliza las energías del 1 al 13 pero su ciclo es de meses y días',NULL,NULL,5),(17,5,'Es el nahual de todas las artes, tejidos y artistas.',NULL,NULL,5),(18,5,'Es el símbolo de la mana y la aurora formada por los primeros rayos del sol.',NULL,NULL,5),(19,5,'El nahual Imox representa',NULL,NULL,10),(20,5,'Es el símbolo de la gran ejergía que creó el universo, la evolución humana y el desarrollo espiritual',NULL,NULL,5),(21,5,'Es un nahual \"muy masculino\", tienen carácter y voz fuertes y es de alguna manera varonil',NULL,NULL,10),(22,6,'Existen varios tipos de calendario maya',NULL,NULL,25),(23,6,'El Tzolkin se conoce como calendario sagrado',NULL,NULL,25),(24,6,'El calendario Chik\'oj forma parte de los calendarios reconocidos por la cultura maya',NULL,NULL,25),(25,6,'La cuenta del calendario Tzolkin involucra 13 números eparejados con nombres de 20 días.',NULL,NULL,25),(26,7,'El Haab es un calendario similar al calendario gregoriano (el que utilizamos)',NULL,NULL,25),(27,7,'El kin equivale al año maya',NULL,NULL,25),(28,7,'El kin zip significa rojo',NULL,NULL,25),(29,7,'Los últimos cinco días del ciclo del haab se conocen como Uayeb',NULL,NULL,25);
